    // unmapped
    private List<AssemblyContigWithFineTunedAlignments>
    buildDataForUnmappedContig() {
        final AlignedContig alignedContig = new AlignedContig("asm000000:tig00000", "".getBytes(), Collections.emptyList());
        return Collections.singletonList(new AssemblyContigWithFineTunedAlignments(alignedContig));
    }

    // one good alignment
    private List<AssemblyContigWithFineTunedAlignments>
    buildDataForNormalContigs() {

        final AlignmentInterval singleAlignment = fromSAMRecordString("asm000000:tig00000\t0\tchr7\t128653321\t60\t417M\t*\t0\t0\\t*\tMD:Z:417\tRG:Z:GATKSVContigAlignments\tNM:i:0\tAS:i:417\tXS:i:214", false);
        final AlignedContig alignedContig = new AlignedContig("asm000000:tig00000", "".getBytes(), Collections.singletonList(singleAlignment));
        return Collections.singletonList(new AssemblyContigWithFineTunedAlignments(alignedContig));
    }

