        AlignedContig simpleIns = fromPrimarySAMRecordString("asm021002:tig00000\t0\tchr13\t28584810\t60\t1133S598M\t*\t0\t0\\t*\tSA:Z:chr13,28584682,+,128M1603S,60,0;\tMD:Z:441T156\tRG:Z:GATKSVContigAlignments\tNM:i:1\tAS:i:593\tXS:i:0", true);
